settings.custom_liquid:
{% if product.description contains 'Reconditioned' %}
    <h4 style="margin:0;">Reconditioned</h4>

  {% elsif product.description contains 'New' %}
    <h4>New Surplus</h4>
  {% elsif product.title contains 'New' %}
    <h4>New Surplus</h4>
  {% elsif product.description contains 'Used' %}
    <h4>Used</h5>
  {% elsif product.title contains 'Used' %}
    <h4>Used</h5>
  {% else %}
    <h4 style="margin:0;">Reconditioned</h4>

  {% endif %}

{% if product.metafields.ges.lead_time %}
{% assign lead_time = product.metafields.ges.lead_time %}
{%- elsif product.title contains 'Reconditioned' %}
{% assign lead_time = 'Ships in 7 - 10 days' %}
{% else %}
{% assign lead_time = 'Ships Next Day' %}

{% endif %}

<div style="background:#A2F5B2; padding:2.5rem 0 1.5rem 0; width: auto;">
  <h3 style="color:#444;line-height:1; padding:0 2rem;">
    <strong>{{ lead_time }}</strong>
  </h3>
</div>
